        // Restore children first (bottom-up)

        for child_snap in &snap.children {
            if child_snap.table_data.entity_rows.table_name == "field" {
                repository_factory::write::create_field_repository(self.transaction)
                    .restore(event_buffer, child_snap)?;
            }
        }
        for child_snap in &snap.children {
            if child_snap.table_data.entity_rows.table_name == "relationship" {
                repository_factory::write::create_relationship_repository(self.transaction)
                    .restore(event_buffer, child_snap)?;
            }
        }

        // Restore this entity's rows
        self.redb_table.restore_rows(&snap.table_data)?;

        // Emit Created events for restored entity IDs
        let restored_ids: Vec<EntityId> = snap
            .table_data
            .entity_rows
            .rows
            .iter()
            .map(|(id, _)| *id)
            .collect();
        if !restored_ids.is_empty() {
            event_buffer.push(Event {
                origin: Origin::DirectAccess(DirectAccessEntity::Entity(EntityEvent::Created)),
                ids: restored_ids.clone(),
                data: None,
            });
        }
        // Emit Updated events for restored relationships
        if !restored_ids.is_empty() {
            event_buffer.push(Event {
                origin: Origin::DirectAccess(DirectAccessEntity::Entity(EntityEvent::Updated)),
                ids: restored_ids,
                data: None,
            });
        }
